"use strict";function code_draw(){var o=$("#canvas").width(),a=$("#canvas").height(),t=document.getElementById("canvas"),r=t.getContext("2d");t.width=o,t.height=a;for(var n="A,B,C,E,F,G,H,J,K,L,M,N,P,Q,R,S,T,W,X,Y,Z,1,2,3,4,5,6,7,8,9,0".split(","),e=n.length,d=[],h=0;h<=3;h++){var l=Math.floor(Math.random()*e),c=30*Math.random()*Math.PI/180,m=n[l];d[h]=m.toLowerCase();var i=10+20*h,M=20+8*Math.random();r.font="bold 23px 微软雅黑",r.translate(i,M),r.rotate(c),r.fillStyle=code_randomColor(),r.fillText(m,0,0),r.rotate(-c),r.translate(-i,-M)}d=d.join(""),$("#canvas").attr("data-code",d);for(h=0;h<=5;h++)r.strokeStyle=code_randomColor(),r.beginPath(),r.moveTo(Math.random()*o,Math.random()*a),r.lineTo(Math.random()*o,Math.random()*a),r.stroke();for(h=0;h<=30;h++){r.strokeStyle=code_randomColor(),r.beginPath();i=Math.random()*o,M=Math.random()*a;r.moveTo(i,M),r.lineTo(i+1,M+1),r.stroke()}}function code_randomColor(){return"rgb("+Math.floor(256*Math.random())+","+Math.floor(256*Math.random())+","+Math.floor(256*Math.random())+")"}$(function(){code_draw(),$("#canvas").on("click",function(){code_draw()})});