"use strict";function checkUsername(){$(".inpu_txt").on("change",function(){var t=$(".inpu_txt").val();/^1[34578]\d{9}$/.test(t)?$(".int_txt").html("手机号码合法"):$(".int_txt").html("手机号码必须是1开头11位的数字")})}function checkPassword(){$(".pw1").on("change",function(){var t=$(".pw1").val();/^\w{3,20}$/.test(t)?$(".int_password").html("密码正确"):$(".int_password").html("数字、26个英文字母或者下划线组成")})}function checkPassword1(){$(".pw2").on("change",function(){var t=$(".pw2").val();/^\w{3,20}$/.test(t)?$(".int_password1").html("密码正确"):$(".int_password1").html("数字、26个英文字母或者下划线组成")})}function heckPasswordAgain(){$(".pw2").on("change",function(){$(".pw1").val()!=$(".pw2").val()&&alert("输入的密码不一致")})}checkUsername(),checkPassword(),checkPassword1(),heckPasswordAgain(),$("#submit").click(function(){$(".inpu_txt")&&$(".pw1")&&$(".pw2")&&$.ajax({method:"post",url:"./php/register.php",data:{name:$(".un").val(),pw:$(".pw").val()},success:function(t){1==t.code?location.href="../login.html":alert(t.msg)},dataType:"json"})});